one</th>										<th>Email</th>										<th>Satus</th>										<th>Action</th>                                    </tr>								</thead>								<tbody>									<?php for ($i = 0; $i < count($GetAllSchool); $i++) { ?>									<tr>										<td><?php echo $GetAllSchool[$i]['name']; ?></td>										<td><?php echo $GetAllSchool[$i]['phone']; ?></td>										<td><?php echo $GetAllSchool[$i]['email']; ?></td>										<td>											<form name="id" method="post" >												<input type="hidden" name="id" value="<?php echo $GetAllSchool[$i]['id'] ?>">												<select style='width:80px' name='status' onChange='this.form.submit()'>													<option value="1" <?php if($GetAllSchool[$i]['status']==1){echo 'selected';} ?>>Active</option>													<option value="0" <?php if($GetAllSchool[$i]['status']==0){echo 'selected';} ?>>In Active</option>												</select>											</form>										</td>										<td class="center">											<div style="float: left">												<a  href="add_edit_school.php?id=<?php echo $GetAllSchool[$i]['id'] ?>"><i class="fa fa-edit"></i></a> 												<a  href="#" onclick="delportfolio('<?php echo $GetAllSchool[$i]['id']; ?>', document.frm1)"> <i class="fa fa-times"></i></a>											</div>										</td>									</tr>							<?php }								echo '</tbody>';									}else{										echo '<tr class="odd gradx"><td colspan="5"><div class="alert alert-info">No results found</div></td></tr>';									} ?>							</table>                            <form name="frm1" action="">                                <input type="hidden" id="hidDelete" name="hidDelete" value="" />                            </form>                        </div>                        <!-- /.table-responsive -->                    </div>                    <!-- /.portlet-body -->                </div>                <!-- /.portlet -->            </div>            <!-- /.col-lg-12 -->        </div>        <!-- /.row --> 